import debug from "debug";

const log = debug("atpls:audio");

export type AudioTrackProperties = {
  volume: number;
};
export type AudioSourceProperties = {
  buffer: any;
};

export type AudioTrack = {};
export type AudioSource = {
  start: (time: number) => void;
  stop: (time: number) => void;
};

export type AudioEngine = {
  createTrack: (props: AudioTrackProperties, destination?: any) => AudioTrack;
  createAudioSource: (props: AudioSourceProperties, destination: any) => AudioSource;
};

export class DebugAudioEngine implements AudioEngine {
  createTrack(props: AudioTrackProperties, destination?: any) {
    return {};
  }
  createAudioSource(props: AudioSourceProperties, destination: any) {
    return new NoAudioSource();
  }
}

export class NoAudioTrack implements AudioTrack {}

export class NoAudioSource implements AudioSource {
  start(time: number) {
    log("start", time);
  }
  stop(time: number) {
    log("stop", time);
  }
}
