import React from "react";
import { Markdown } from "../shared/Markdown";
import { Audioset } from "../../lib/audioset";
import { LoadState } from "../../lib/loader";

type PreviewProps = {
  audioset: Audioset;
  loadState: LoadState;
  onStart: () => void;
};

const Preview = ({ audioset, loadState, onStart }: PreviewProps) => {
  const status = loadState.status;

  return (
    <div className="Preview">
      <img alt={audioset.meta.title} src={audioset.meta.logo_url} />

      <div className="inside">
        <div>{audioset.meta.description}</div>
        <Markdown markdown={audioset.meta.readme} />
        {status === "working" && (
          <div>
            <h2>Cargando...</h2>
            {loadState.completed}/{loadState.total}
          </div>
        )}
        {status === "init" && <button onClick={onStart}>Entrar</button>}
      </div>
    </div>
  );
};

export default Preview;
