import { debounce } from "lodash.debounce";
import Visuals from "./visuals";
import Display from "./display";
import { Audioset } from "../Player";

export default function createVisuals(audioset: Audioset, el) {
  const display = new Display(el);
  const visuals = new Visuals(audioset, display);

  const mode = audioset.visuals.mode;

  if (mode === "map") {
    fetch(audioset.visuals.geomap.url).then((response) => response.json()).then((data) => visuals.setGeodata(data));
  } else if (mode === "panel") {
    visuals.setup();
  }

  window.addEventListener(
    "resize",
    debounce(() => {
      visuals.resizeSvg(el);
    }),
  );

  return {
    start: (clipId) => visuals.show(clipId),
    stop: (clipId) => visuals.hide(clipId),
  };
}
