import React, { useState } from "react";
import { player } from "../../player";
import { FullscreenIcon, Stop } from "../shared/Icons";
import "./Footer.css";
import { useFullscreen } from "./useFullscreen";

export const Footer = () => {
  const [isFullscreen, setFullscreen] = useState(false);
  const fullscreen = useFullscreen(isOpen => setFullscreen(isOpen));
  const requestFullScreen = () => {
    fullscreen.open();
  };
  return (
    <div className="footer PlayerFooter">
      <div className="left">...</div>
      <div className="center" />
      <div className="right">
        <span>FULL: {isFullscreen ? "SIII" : "NO   :_("}</span>
        <button
          className="btn-link"
          title="Full screen"
          onClick={requestFullScreen}
        >
          <FullscreenIcon />
        </button>
        <button
          className="btn-link"
          title="Stop all"
          onClick={() => player.control.stopAll(0)}
        >
          <Stop />
        </button>
      </div>
    </div>
  );
};
