import { Clip } from "./ClipMetadata";
export { Clip } from "Clip";

export type Track = {
  id: string;
  name: string;
  color: string;
  clipIds: string[];
  position: number;
  volume?: number;
};

export type AudiosetMetadata = {
  title: string;
  description: string;
  path: string;
  parent_path: string;
  logo_url: string;
  readme: string;
};

/**
 * The aaudioset metadata extended to include the audioset id and publish path
 * It's used in audioset project to list audiosets
 */
export type AudiosetReference = AudiosetMetadata & {
  id: string;
  publish_path: string;
};

export interface AudiosetData {
  type: string;
  meta: AudiosetMetadata;
}

/**
 * An AudiosetProject is a group of audiosets
 */
export interface AudiosetProject extends AudiosetData {
  type: "project";
  audiosets: AudiosetReference[];
}

export interface AudiosetIndexes {
  clipById: Record<string, Clip>;
  trackById: Record<string, Track>;
  clipIdsOfTrack: Record<string, string[]>;
  trackIdOfClip: Record<string, string>;
}

export interface AudiosetVisuals {
  mode: "map";
  geomap?: {
    url: string;
    scaleFactor: number;
    center: { x: number; y: number };
  };
}

export interface Audioset extends AudiosetData {
  type: "audioset";
  id: string;
  tracks: Track[];
  clips: Clip[];
  visuals: AudiosetVisuals;
  index: AudiosetIndexes;
}

export const EmptyAudioset: Audioset = {
  type: "audioset",
  id: "none",
  meta: {
    title: "",
    description: "",
    path: "",
    parent_path: "",
    logo_url: "",
    readme: "",
  },
  visuals: {
    mode: "map",
  },
  tracks: [],
  clips: [],
  index: {
    clipById: {},
    trackById: {},
    clipIdsOfTrack: {},
    trackIdOfClip: {},
  },
};

export function createAudiosetIndexes(audioset: Audioset): Audioset {
  const index: AudiosetIndexes = {
    clipById: {},
    trackById: {},
    clipIdsOfTrack: {},
    trackIdOfClip: {},
  };
  audioset.clips.forEach((clip) => {
    index.clipById[clip.id] = clip;
    index.trackIdOfClip[clip.id] = clip.trackId;
  });
  audioset.tracks.forEach((track) => {
    index.trackById[track.id] = track;
    index.clipIdsOfTrack[track.id] = track.clipIds;
  });

  audioset.index = index;
  return audioset;
}
