import debug from "debug";
import { useEffect } from "react";
import { player } from "../../player";

const log = debug("atpls:useStartStopAudio");
export function useStartStopAudio() {
  useEffect(() => {
    return () => {
      log("bye!");
      player.control.stopAll(0);
    };
  }, []);
}
