/**
 * A Clip is something with sound, mapped at space... etc√©tera...
 */
export interface Clip extends ClipMetadata, ClipVisuals {
  id: string;
  keyMap: string;
  trackId: string;
  audio: {
    beats: number;
    volume?: number;
  };
  resources: ClipResources;
}

interface ClipMetadata {
  title: string;
  album: string;
  year: string;
  artist?: string;
  place?: string;
  country?: string;
}

interface ClipVisuals {
  color: string;
  position: [number, number];
}

interface ClipResources {
  audio: {
    mp3: string;
    ogg?: string;
    wav?: string;
  };
  cover: {
    small: string;
    thumb: string;
  };
}
