import { RefObject, useEffect } from "react";

const ref: RefObject<HTMLDivElement> = { current: null };

export function scrollTo(x: number, y: number) {
  if (ref.current) {
    console.log("TOP!!", ref);
    const el = ref.current;
    el.scrollTo(x, y);
  }
}

export function useScrollTop() {
  useEffect(() => {
    scrollTo(top);
  }, []);
  return ref;
}
