import { debounce } from "lodash";
import Visuals from "./visuals";
import Display from "./display";
import { Audioset } from "../Audioset";

export function createVisuals(audioset: Audioset, el: any) {
  const display = new Display(el);
  const visuals = new Visuals(audioset, display);

  setupVisuals(audioset, visuals);

  const handleResize = debounce(() => {
    visuals.resizeSvg();
  });

  window.addEventListener("resize", handleResize);
}
function setupVisuals(audioset: Audioset, visuals: Visuals) {
  if (audioset.visuals.mode === "map") {
    fetch(audioset.visuals.geomap.url).then((response) => response.json()).then((data) => visuals.setGeodata(data));
  } else {
    visuals.setup();
  }
}
