import { getAudioContext } from "./AudioContext";

interface Config {
  bpm: number;
  quantize: number;
}

export class TimeManager {
  private count: number = 0;
  private startedAt: number = 0;
  private context?: AudioContext;

  constructor({ bpm, quantize }: Config) {
    this.bpm = bpm;
    this.quantize = quantize;
    getAudioContext().then(ctx => (this.context = ctx));
  }

  public startTime(time: number) {
    if (!this.context) {
      return time;
    }

    time = time || this.context.currentTime;
    this.count += 1;

    if (this.count === 1) {
      this.startedAt = time;
    }
    return time;
  }

  public stopTime(time: number) {
    if (!this.context) {
      return time;
    }

    time = time || this.context.currentTime;
    this.count -= 1;

    if (this.count === 0) {
      this.startedAt = 0;
    }
    return time;
  }
}
const BEATS = 1;

/**
 * Calculate the time remaining until the next beat
 *
 * @param {*} bpm
 * @param {*} now
 * @param {*} startedAt
 * @param {*} beats
 */
export function quantize(
  bpm: number,
  now: number,
  startedAt: number,
  beats = BEATS,
): number {
  if (!startedAt) {
    return 0;
  }

  const factor = bpm / (60 * beats);
  const absolute = now - startedAt;
  if (absolute === 0) {
    return 0;
  }
  const inBeats = absolute * factor;
  const mod = inBeats % 1;
  const offsetTime = (1 - mod) / factor;
  return offsetTime;
}
