import React, { useEffect, useRef } from "react";
export const Scroll = ({ children }: any) => {
  const ref = useRef<HTMLDivElement>();
  useEffect(() => {
    if (ref.current) { ref.current.scrollTo(0, 0); }
  }, [ref]);
  return (
    <div ref={ref} id="scroll" className="scroll">
      {children}
    </div>
  );
};
