  audioset: Audioset;
}
export const Readme = ({ closed, audioset }: ReeadmeProps) => {
  const [isVisible, toggleVisibility] = useReducer(x => !x, false);

  return (
    <div className="Readme">
      <Markdown markdown={audioset.meta.readme} />
    </div>
  );
};
