import React from "react";
import { useScroll } from "./useScroll";

/**
 *
 */
export const Scroll = ({ children }: any) => {
  const ref = useScroll();
  return (
    <div ref={ref} id="scroll" className="scroll">
      {children}
    </div>
  );
};
