import Observer from "resize-observer-polyfill";

type ResizeListener = (width: number, heigth: number) => void;
export const ResizeObserver = Observer;

export function addResizeObserver(
  element: any,
  callback: ResizeListener,
): ResizeObserver {
  const resizeObserver = new ResizeObserver(entries => {
    if (!Array.isArray(entries)) {
      return;
    }
    // Since we only observe the one element, we don't need to loop over the array
    if (!entries.length) {
      return;
    }
    const entry = entries[0];
    const width = entry.contentRect.width;
    const height = entry.contentRect.height;
    callback(width, height);
  });
  resizeObserver.observe(element);
  return resizeObserver;
}
