import { getAudioContext } from "../AudioContext";

export class TimeManager {
  protected playingClipsCount: number = 0;
  protected startedAt: number = 0;

  constructor() {
    getAudioContext().then(ctx => (this.context = ctx));
  }

  public startTime(time: number) {
    if (this.playingClipsCount === 0) {
      this.startedAt = time;
    }
    this.playingClipsCount += 1;
    if (this.playingClipsCount === 0) {
      this.startedAt = 0;
    }
    return time;
  }
}
