import debug from "debug";
import { AudioContext, TAudioContextState } from "standardized-audio-context";

let context: AudioContext | undefined;
let state: TAudioContextState = "suspended";

const log = debug("atpls:context");

function setState(TAudioContextState: newState) {
  state = newState;
}

function getAudioContext() {
  if (context === undefined) {
    context = new AudioContext();
    state = context.state;
    context.onstatechange = function(event: Event) {
      setState(context.state);
    };
  }
  return context;
}

/**
 * @see https://developers.google.com/web/updates/2017/09/autoplay-policy-changes#webaudio
 */
export async function getActiveAudioContext(): Promise<AudioContext> {
  context = getAudioContext();

  if (context.state !== "running" && context.resume) {
    log("waiting for context...");
    return context
      .resume()
      .then(() => startAudioContext(context as AudioContext));
  } else {
    return context;
  }
}

function startAudioContext(ctx: AudioContext): AudioContext {
  log("start context");
  // iOS hack. See https://github.com/tambien/StartAudioContext/blob/master/StartAudioContext.js
  const buffer = ctx.createBuffer(1, 1, ctx.sampleRate);
  const source = ctx.createBufferSource();
  source.buffer = buffer;
  source.connect(ctx.destination);
  source.start(0);
  return ctx;
}
