import React, { useReducer } from "react";
import { Clip } from "./Clip";
import { Audioset } from "../../lib/audioset";

interface Hash<T> {
  [key: string]: T;
}

function reducer(state: Hash<boolean>, id: string) {
  state[id] = !!!state[id];
  return state;
}

type ClipsProps = {
  audioset: Audioset;
};

export const Clips = ({ audioset }: ClipsProps) => {
  const [selected, dispatch] = useReducer(reducer, {});

  if (!audioset || !audioset.tracks) return <></>;

  return (
    <div className="Clips">
      {audioset.tracks.map(track => (
        <div className="Track" style={{ backgroundColor: track.color }}>
          <h2 className="title">{track.name}</h2>
          {track.clipIds.map(clipId => (
            <Clip key={clipId} id={clipId} clip={audioset.clipsById[clipId]} />
          ))}
        </div>
      ))}
    </div>
  );
};
