import React from "react";
import { Audioset, Track } from "../../audioset";
import { player } from "../../player";
import { ControlState } from "../../player/AudiosetControl";
import { Clip } from "./Clip";

export function TrackView(track: Track, audioset: Audioset, state: ControlState): JSX.Element {
  return (<div key={track.id} className="Track" style={{ backgroundColor: track.color }}>
    <h2 className="title">{track.name}</h2>
    {track.clipIds.map(clipId => (<Clip key={clipId} clip={audioset.index.clipById[clipId]} isActive={state.clips[clipId].state === "playing"} onClick={() => player.control.toggleClip(clipId, 0)} />))}
  </div>);
}
