import React, { useState } from "react";
import { player } from "../../player";
import { FullscreenIcon, Stop } from "../shared/Icons";
import "./Footer.css";
import { Fullscreen, useFullscreen } from "./useFullscreen";

export const Footer = ({ fullscreen }: { fullscreen: Fullscreen }) => {
  return (
    <div className="footer PlayerFooter">
      <div className="left">...</div>
      <div className="center" />
      <div className="right">
        <button
          className="btn-link"
          title="Full screen"
          onClick={fullscreen.toggle}
        >
          <FullscreenIcon />
        </button>
        <button
          className="btn-link"
          title="Stop all"
          onClick={() => player.control.stopAll(0)}
        >
          <Stop />
        </button>
      </div>
    </div>
  );
};
