import debug from "debug";
import { IAudioContext } from "standardized-audio-context";
import { Audioset } from "../../audioset";
import { ControlCommand, Effects } from "../Control";
import { AudioEngine, AudioSource, AudioTrack } from "./Audio";
import { AudioContextEngine } from "./AudioContextEngine";

const log = debug("atpls:sampler");

export interface SampleBuffers {
  getBuffer(clipId: string): any;
}

export function createAudioEffects(
  audioset: Audioset,
  ctx: IAudioContext,
  buffers: SampleBuffers,
): AudioEffects {
  const audio = new AudioContextEngine(ctx);
  return new AudioEffects(audioset, buffers, audio);
}

class AudioEffects implements Effects {
  public sampler: Sampler;
  constructor(
    private audioset: Audioset,
    private ctx: IAudioContext,
    buffers: SampleBuffers,
  ) {
    log("init AudioEffects %s", audioset.meta.title);
    this.sampler = new Sampler(buffers, ctx);
    this.sampler.initTracks(audioset.tracks);
  }

  public attach() {
    // nothing to do?
  }

  public detach() {
    this.sampler.disconnect();
  }

  public run(command: ControlCommand) {
    const time = command.time;
    switch (command.command) {
      case "startClip":
        const clipId = command.clipId;
        const trackId = this.audioset.index.trackIdOfClip[clipId];
        log("start %s", clipId);
        return this.sampler.start(clipId, trackId, time);
      case "stopClip":
        log("stop %s", command.clipId);
        return this.sampler.stop(command.clipId, time);
      default:
    }
  }
}

interface SamplerTrack {
  id: string;
  name: string;
  volume?: number;
}
class Sampler {
  private master: AudioTrack;
  private tracks: Record<string, AudioTrack> = {};
  private audioSources: Record<string, AudioSource | undefined> = {};

  constructor(private buffers: SampleBuffers, private audio: AudioEngine) {
    this.master = audio.createTrack("master", { volume: 0.8 });
  }

  public initTracks(tracks: SamplerTrack[]) {
    tracks.forEach(track => {
      this.tracks[track.id] = this.audio.createTrack(
        track.name,
        { volume: 1 },
        this.master,
      );
    });
  }

  public connect() {
    // nothing to do?
  }

  public disconnect() {
    Object.keys(this.tracks).forEach(trackId => {
      this.tracks[trackId].disconnect();
    });
  }

  public start(clipId: string, trackId: string, time: number) {
    if (this.audioSources[clipId]) {
      return;
    }

    const buffer = this.buffers.getBuffer(clipId);
    const track = this.tracks[trackId];
    const source = this.audio.createAudioSource({ buffer }, track);
    this.audioSources[clipId] = source;

    source.start(time);
  }

  public stop(clipId: string, time: number) {
    const source = this.audioSources[clipId];
    if (source !== undefined) {
      source.stop(time);
    }
    this.audioSources[clipId] = undefined;
  }
}
