import React, { useEffect } from "react";
export const Scroll = ({ children }: any) => {
  useEffect(() => {
    const el = document.getElementById("#scroll");
    if (el) {
      el.scroll.y=0
      el.scrollTo(0, 0);
    }
  }, []);
  return (
    <div id="scroll" className="scroll">
      {children}
    </div>
  );
};
