/**
 * Possible Player states
 */
export type PlayinStatus = "stopped" | "playing"; // | "playScheduled" |  "stopScheduled";

export interface ClipPlayinStatus {
  readonly satus: PlayinStatus;
}
export interface TrackPlayinStatus {
  readonly satus: PlayinStatus;
  readonly volume: number;
}

export interface ControlState {
  clips: ClipPlayinStatusByClipId;
  tracks: TrackPlayinStatusByTrackId;
}

export type ClipPlayinStatusByClipId = Record<string, ClipPlayinStatus>;
export type TrackPlayinStatusByTrackId = Record<string, TrackPlayinStatus>;
