import { useEffect, useState } from "react";
import { player } from "../../player";

export function useResourceLoader() {
  const [load, setLoad] = useState(player.resources.status);
  useEffect(
    () =>
      player.onResourceStatusChanged(status => {
        setLoad(status);
      }),
    [],
  );
  return { load };
}
