import { RefObject, useEffect, useRef } from "react";

const ref: RefObject<HTMLDivElement> = { current: null };

export function useScrollTop() {
  const ref = useRef<HTMLDivElement>(null);
  useEffect(() => {
    if (ref.current) {
      console.log("TOP!!", ref);
      const el = ref.current;
      el.scrollTo(0, 0);
    }
  }, []);
  return ref;
}
