import React, {  useState, useEffect } from "react";
import { Clip } from "./Clip";
import { Audioset, player } from "../../Audioset";
import { ControlState } from "../../Player/AudiosetControl";


type ClipsProps = {
  audioset: Audioset;
};

export const Clips = ({ audioset }: ClipsProps) => {
  const state = useControlState()

  if (!audioset || !audioset.tracks) return <></>;

  return (
    <div className="Clips">
      {audioset.tracks.map(track => (
        <div key={track.id} className="Track" style={{ backgroundColor: track.color }}>
          <h2 className="title">{track.name}</h2>
          {track.clipIds.map(clipId => (
            <Clip key={clipId}  clip={audioset.index.clipById[clipId]}
            isActive={state.clips[clipId].state === 'playing'}
            onClick={() => player.control.toggleClip(clipId, 0)}
            />
          ))}
        </div>
      ))}
    </div>
  );
};

function useControlState(): ControlState {
  const [state, setState] = useState(player.control.getState())
  useEffect(() => player.onControlStateChanged((state: any) => {
    setState(state)
  }))
  return state;
}
