import { useEffect, useState } from "react";
import * as screen from "screenfull";

export function useFullscreen(onChange: (isOpen: boolean) => void) {
  const request = () => undefined;
  const [isOpen, setIsOpen] = useState();
  useEffect(() => {
    if (screen.isEnabled) {
      screen.on("change", setIsOpen);
    }
  }, []);
  return { isOpen, request, exit };
}

export function useFullscreenOld(onChange: (isOpen: boolean) => void) {
  const [fullscreen, setFullscreen] = useState();
  const open = () => setFullscreen(Date.now());

  if (screen.isEnabled) {
    screen.on("change", () => onChange(screen.isEnabled));
  }

  useEffect(() => {
    if (!screen.isEnabled) {
      return;
    }
    if (fullscreen) {
      screen.request();
    } else {
      screen.exit();
    }
  }, [fullscreen]);

  return { open };
}
