import { useCallback, useEffect, useState } from "react";
import * as screen from "screenfull";

const request = () => screen.isEnabled && screen.request();
const exit = () => screen.isEnabled && screen.exit();

export function useFullscreen() {
  const [isFull, setIsFull] = useState();
  const detach = useCallback(() => {
    if (screen.isEnabled) {
      screen.off("change", setIsFull);
    }
  }, []);
  useEffect(() => {
    if (screen.isEnabled) {
      screen.on("change", setIsFull);
    }
    return detach;
  }, []);
  return { isFull, request, exit };
}
