import React, { useReducer } from "react";
import { Audioset } from "../../audioset";
import { Markdown } from "../shared/Markdown";

interface ReeadmeProps {
  closed: boolean;
  content: string;
}

export const Readme = ({ closed, content }: ReeadmeProps) => {
  const [isVisible, toggleVisibility] = useReducer(x => !x, false);

  return (
    <div className="Readme">
      <Markdown markdown={ssummary(content)} />
    </div>
  );
};

function ssummary(content: string) {
  const summary = content.slice(0, content.indexOf("\n"));
  return 'dsad';
}
