import React from "react";
import { Link } from "react-router-dom";
import { BundleMetadata } from "../../audioset";
import "./Header.css";
import { ArrowLeft } from "./Icons";

interface HeaderProps {
  meta?: BundleMetadata;
}

const getAudiosetPath = (path: string) => (path ? `/set/${path}` : `/`);

/**
 * The header
 */
export const Header = ({ meta }: HeaderProps) =>
  meta && meta.path !== "index" ? (
    <BundleHeader title={meta.title} path={getAudiosetPath(meta.parent_path)} />
  ) : (
    <LogoHeader />
  );

export interface BundleHeaderProps {
  title: string;
  path?: string;
}

export const BundleHeader = ({ title, path }: BundleHeaderProps) => (
  <div className="Header">
    <Link className="navigation" to={path || "/"}>
      <ArrowLeft />
      <h1>{title}</h1>
    </Link>
  </div>
);

export const LogoHeader = () => (
  <div className="Header">
    <div className="logo">
      <Link to="/">
        <img
          className="play-logo"
          src="/play-logo.png"
          alt="Play antropoloops"
        />
      </Link>
    </div>
  </div>
);
