import React, { useRef, useState, useEffect, RefObject } from "react";
import { Audioset } from "../../Audioset";
import { player } from "../../Player";
import { ControlCommand } from "../../Player/AudiosetControl";

export const Visuals = ({ audioset }: VisualsProps) => {
  const ref = useRef<HTMLDivElement>(null);
  const visuals = useVisuals(audioset, ref);
  return <div id="visuals" ref={ref} />;
};
interface VisualsProps {
  audioset: Audioset;
}

interface VisualsModule {
  createVisuals(audioset: Audioset, el: HTMLDivElement): {};
}

function useVisuals(audioset: Audioset, ref: RefObject<HTMLDivElement>) {
  const [visuals, setVisuals] = useState();

  useEffect(
    () => {
      import("../../visuals/index").then(({ createVisuals }) => {
        if (ref.current && audioset) {
          const visuals = createVisuals(audioset, ref.current);
          player.onCommand((command: ControlCommand) => {});
        }
      });
    },
    [audioset, ref.current],
  );

  return { visuals };
}
