import { useCallback, useEffect, useState } from "react";
import * as screen from "screenfull";

const request = () => screen.isEnabled && screen.request();
const exit = () => screen.isEnabled && screen.exit();

export function useFullscreen() {
  const [isFull, setIsFull] = useState();
  const detach = useCallback(() => {
    if (screen.isEnabled) {
      screen.off("change", setIsFull);
    }
  }, []);
  useEffect(() => {
    if (screen.isEnabled) {
      screen.on("change", setIsFull);
    }
    return detach;
  }, []);
  return { isFull, request, exit };
}

export function useFullscreenOld(onChange: (isFull: boolean) => void) {
  const [fullscreen, setFullscreen] = useState();
  const open = () => setFullscreen(Date.now());

  if (screen.isEnabled) {
    screen.on("change", () => onChange(screen.isEnabled));
  }

  useEffect(() => {
    if (!screen.isEnabled) {
      return;
    }
    if (fullscreen) {
      screen.request();
    } else {
      screen.exit();
    }
  }, [fullscreen]);

  return { open };
}
