import { useEffect, useRef } from "react";
export function useScroll() {
  const ref = useRef<HTMLDivElement>(null);
  useEffect(() => {
    if (ref.current) {
      const el = ref.current;
      el.scrollTo(0, 0);
    }
  }, []);
  return ref;
}
