/** @jsx jsx */
import { useState, useEffect, useRef } from "react";
import { css, jsx } from "@emotion/core";
import { Audioset } from "../../Player";

interface VisualsProps {
  audioset: Audioset;
}

export const Visuals = ({ audioset }: VisualsProps) => (
  <div ref={ref} className="Visuals" css={displayCss}>
    map here
  </div>
);

const displayCss = css`
  background-color: blue;

  @media (orientation: landscape) {
    height: 100vh;
  }

  @media (orientation: portrait) {
    height: calc(100vw * (9 / 16));
  }
`;

interface VisualsModule {
  createVisuals(audioset: Audioset, el: HTMLDivElement): {};
}

function useVisuals(audioset: Audioset) {
  const ref = useRef();
  const [visuals, setVisuals] = useState();

  useEffect(
    () => {
      import("../../visuals/index").then((m: unknown) => {
        if (ref.current) {
          const el = ref.current as HTMLDivElement;
          const visuals = m as VisualsModule;
          setVisuals(audioset && visuals.createVisuals(audioset, el));
        }
      });
    },
    [audioset, ref.current],
  );

  return { visuals, ref };
}
