/** @jsx jsx */
import { css, jsx } from "@emotion/core";

export const Visuals = () => (
  <div className="Visuals" css={displayCss}>
    map here
  </div>
);

const displayCss = css`
  background-color: blue;

  @media (orientation: landscape) {
    height: 100vh;
  }

  @media (orientation: portrait) {
    height: calc(100vw * (9 / 16));
  }
`;

function useVisuals(audioset: Audioset) {
  const [visuals, setVisuals] = useState();

  useEffect(
    () => {
      import("../../visuals/index").then((visuals) => {
        setVisuals(audioset && visuals.init(audioset));
      });
    },
    [audioset],
  );

  return visuals;
}
